import React, { useState, useEffect } from 'react';

function Admin() {
  const [users, setUsers] = useState([]);
  const [editUserId, setEditUserId] = useState(null);
  const [editedEmail, setEditedEmail] = useState('');
  const [editedPassword, setEditedPassword] = useState('');

  useEffect(() => {
    // Fetch users from API
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.sheety.co/182b17ec2dcc0a8d3be919b2baff9dfc/gamerift/folha1');
        if (response.ok) {
          const data = await response.json();
          setUsers(data.folha1);
        } else {
          console.error('Failed to fetch users');
        }
      } catch (error) {
        console.error('Error during fetch:', error.message);
      }
    };

    fetchData();
  }, []); // Run only once on component mount

  const handleEditUser = (userId) => {
    // Set the user ID to be edited and pre-fill the form with existing values
    const userToEdit = users.find(user => user.id === userId);
    setEditUserId(userId);
    setEditedEmail(userToEdit.email);
    setEditedPassword(userToEdit.password);
  };

  const handleUpdateUser = async () => {
    try {
      // Assume your API supports PUT method for user update
      const response = await fetch(`https://api.sheety.co/182b17ec2dcc0a8d3be919b2baff9dfc/gamerift/folha1/${editUserId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: editedEmail, password: editedPassword }),
      });

      if (response.ok) {
        // Update the local state with the edited user
        setUsers((prevUsers) => prevUsers.map(user => (user.id === editUserId ? { ...user, email: editedEmail, password: editedPassword } : user)));
        console.log('User updated successfully');
        // Clear the edit mode
        setEditUserId(null);
        setEditedEmail('');
        setEditedPassword('');
      } else {
        console.error('Failed to update user');
      }
    } catch (error) {
      console.error('Error during user update:', error.message);
    }
  };

  return (
    <div>
      <h2>User Management</h2>
      <ul>
        {users.map((user) => (
          <li key={user.id}>
            {editUserId === user.id ? (
              <>
                <input type="text" value={editedEmail} onChange={(e) => setEditedEmail(e.target.value)} />
                <input type="text" value={editedPassword} onChange={(e) => setEditedPassword(e.target.value)} />
                <button onClick={handleUpdateUser}>Update</button>
              </>
            ) : (
              <>
                <span>{`ID: ${user.id}, Email: ${user.email}, Password: ${user.password}`}</span>
                <button onClick={() => handleEditUser(user.id)}>Edit</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Admin;
