import React, { Component } from 'react';
import { AppContext } from '../App';
import './main.css';
import Header from './Header';
import SideMenu from '../components/SideMenu';
import Home from './Home';
import Categories from './Categories';
import MyLibrary from './MyLibrary';
import Bag from './Bag';

class Main extends Component {
  constructor(props) {
    super(props);

    this.state = {
      active: false,
      games: [],
    };

    this.homeRef = React.createRef();
    this.categoriesRef = React.createRef();
    this.libraryRef = React.createRef();
    this.bagRef = React.createRef();
  }

  componentDidMount() {
    this.fetchData();
  }

  fetchData() {
    fetch('http://localhost:3000/api/gamesData.json')
      .then(res => res.json())
      .then(data => {
        this.setState({ games: data });
      })
      .catch(e => console.log(e.message));
  }

  handleToggleActive = () => {
    this.setState(prevState => ({ active: !prevState.active }));
  };

  handleSectionActive = target => {
    const sections = [
      { ref: this.homeRef, name: 'home' },
      { ref: this.categoriesRef, name: 'categories' },
      { ref: this.libraryRef, name: 'library' },
      { ref: this.bagRef, name: 'bag' },
    ];

    sections.forEach(section => {
      section.ref.current.classList.remove('active');
      if (section.name === target) {
        section.ref.current.classList.add('active');
      }
    });
  };

  render() {
    const { library, bag } = this.context;

    return (
      <main>
        <SideMenu active={this.state.active} sectionActive={this.handleSectionActive} />
        <div className={`banner ${this.state.active ? 'active' : undefined}`}>
          <Header toggleActive={this.handleToggleActive} />
          <div className="contain-fluid">
            {this.state.games.length > 0 && <Home games={this.state.games} reference={this.homeRef} />}
            {this.state.games.length > 0 && (
              <Categories games={this.state.games} reference={this.categoriesRef} />
            )}
            {this.state.games.length > 0 && (
              <MyLibrary games={library} reference={this.libraryRef} />
            )}
            {this.state.games.length > 0 && <Bag games={bag} reference={this.bagRef} />}
          </div>
        </div>
      </main>
    );
  }
}

Main.contextType = AppContext;

export default Main;
