import React, { useState, useEffect } from 'react';

function Admin() {
  const [users, setUsers] = useState([]);
  const [editUserId, setEditUserId] = useState(null);
  const [editedUser, setEditedUser] = useState({ id: null, email: '', password: '' });

  useEffect(() => {
    // Fetch users from API
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.sheety.co/182b17ec2dcc0a8d3be919b2baff9dfc/gamerift/folha1');
        if (response.ok) {
          const data = await response.json();
          setUsers(data.folha1);
        } else {
          console.error('Failed to fetch users');
        }
      } catch (error) {
        console.error('Error during fetch:', error.message);
      }
    };

    fetchData();
  }, []); // Run only once on component mount

  const handleEditUser = (userId) => {
    // Set the user ID to be edited and pre-fill the form with existing values
    const userToEdit = users.find(user => user.id === userId);
    setEditUserId(userId);
    setEditedUser(userToEdit);
  };

  const handleUpdateUser = async () => {
    try {
      // Assume your API supports PUT method for user update
      const response = await fetch(`https://api.sheety.co/182b17ec2dcc0a8d3be919b2baff9dfc/gamerift/folha1/${editUserId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(editedUser),
      });

      if (response.ok) {
        // Update the local state with the edited user
        setUsers((prevUsers) =>
          prevUsers.map((user) => (user.id === editUserId ? { ...user, ...editedUser } : user))
        );
        console.log('User updated successfully');
        // Clear the edit mode
        setEditUserId(null);
        setEditedUser({ id: null, email: '', password: '' });
      } else {
        console.error('Failed to update user');
      }
    } catch (error) {
      console.error('Error during user update:', error.message);
    }
  };

  const handleDeleteUser = async (userId) => {
    try {
      // Assume your API supports DELETE method for user deletion
      const response = await fetch(`https://api.sheety.co/182b17ec2dcc0a8d3be919b2baff9dfc/gamerift/folha1/${userId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        // Remove the deleted user from the local state
        setUsers((prevUsers) => prevUsers.filter((user) => user.id !== userId));
        console.log('User deleted successfully');
      } else {
        console.error('Failed to delete user');
      }
    } catch (error) {
      console.error('Error during user deletion:', error.message);
    }
  };

  const handleCancelEdit = () => {
    // Clear the edit mode
    setEditUserId(null);
    setEditedUser({ id: null, email: '', password: '' });
  };

  return (
    <div>
      <h2>User Management</h2>
      <ul>
        {users.map((user) => (
          <li key={user.id}>
            {editUserId === user.id ? (
              <>
                <input
                  type="text"
                  value={editedUser.email}
                  onChange={(e) => setEditedUser({ ...editedUser, email: e.target.value })}
                />
                <input
                  type="text"
                  value={editedUser.password}
                  onChange={(e) => setEditedUser({ ...editedUser, password: e.target.value })}
                />
                <button onClick={handleUpdateUser}>Update</button>
                <button onClick={handleCancelEdit}>Cancel</button>
              </>
            ) : (
              <>
                <span>{`ID: ${user.id}, Email: ${user.email}, Password: ${user.password}`}</span>
                <button onClick={() => handleEditUser(user.id)}>Edit</button>
                <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Admin;
