// import Bootstrap
import "bootstrap/dist/css/bootstrap.min.css";
// import Bootstrap Icons
import "bootstrap-icons/font/bootstrap-icons.css";

import React, { useState } from "react";
import "./App.css";
import Main from "./pages/Main";
import Login from "./Login"; // Importa o componente de login
import { AppContext } from "./App";

function App() {
  const [library, setLibrary] = useState([]);
  const [bag, setBag] = useState([]);
  const [user, setUser] = useState(null); // Estado para armazenar o usuário autenticado

  // Função para lidar com o login bem-sucedido
  const handleLogin = (username) => {
    // Neste exemplo, apenas definimos o usuário no estado
    // Em uma aplicação real, você autenticaria o usuário e obtendo um token de autenticação, etc.
    setUser(username);
  };

  // Se o usuário não estiver autenticado, renderiza a página de login
  if (!user) {
    return (
      <Login
        onLogin={handleLogin} // Passa a função de login como prop para o componente de login
      />
    );
  }

  // Se o usuário estiver autenticado, renderiza a página principal
  return (
    <AppContext.Provider value={{ library, setLibrary, bag, setBag }}>
      <Main />
    </AppContext.Provider>
  );
}

export default App;
