import React, { useState, useContext } from 'react';
import './header.css';
import { AppContext } from '../App';
import Bag from './Bag'; 
import MyLibrary from './MyLibrary';
import Categories from './Categories';
import Home from './Home';

const header_session = () => {
    return (
      <div>
        {/* Renderiza ComponenteA */}
        <Bag />
        
        {/* Renderiza ComponenteB */}
        <MyLibrary />
        <Categories />
        <Home />
      </div>
    );
  };

function estadoClick(url){

  window.location.replace(url);
  
}

function Header_session({ toggleActive }) {
  const { library, bag } = useContext(AppContext);
  const [menu, setMenu] = useState(false);
  const toggleMenu = () => {
    setMenu(!menu);
    toggleActive();
  };

  return (
    <header>
      <div className="d-flex align-items-center">
        <a
          href="#"
          className={`menu ${menu ? 'active' : undefined}`}
          onClick={toggleMenu}
        >
          <i className="bi bi-sliders"></i>
        </a>
      </div>
      <div className="userItems">
        <a href="#" className="icon">
          <i className="bi bi-heart-fill"></i>
          <span className="like">{library.length}</span>
        </a>
        <a href="#" className="icon">
          <i className="bi bi-bag-fill"></i>
          <span className="bag">{bag.length}</span>
        </a>
        <div className="avatar">
          <div className="user">
            <button class="bots" onClick={() => estadoClick('/')} >Log out</button>
          </div>
        </div>
      </div>
    </header>
  );
}

export default Header_session;
