import React, { Component } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';

class Login extends Component {

    state={
        email: "",
        password: "",
    }

    handleEmailChange(evt){
        this.setState({email: evt.target.value});
      }

      handlePasswordChange(evt){
        this.setState({password: evt.target.value});
      }

      handleLogin = async () => {
        const baseApiUrl  = 'https://api.sheety.co/182b17ec2dcc0a8d3be919b2baff9dfc/gamerift/folha1/login';
        const credentials = btoa(`${this.state.email}:${this.state.password}`);
        const authUrl = `${baseApiUrl}?auth=${credentials}`;
                
        fetch(authUrl)
        .then((response) => {
        if (response.ok) {
            return response.json();
            console.log("autenticado");
        } else {
            throw new Error('Authentication failed');
        }
        })
        .then((json) => {
        // Do something with the data
        console.log(json.folha1S);
        })
        .catch((error) => {
        console.error('Error during authentication:', error.message);
        });}


  render() {
    return (
      <div className="main_div">
        <Container className="d-flex justify-content-center align-items-center" style={{ height: '100vh', backgroundColor: 'transparent', marginTop: '3%' }}>
          <Row>
            <Col xs={12} md={12} className="bg-light rounded p-5">
              <h1 className="text-center mb-5" style={{ textAlign: 'center' }}>Iniciar Sess√£o</h1>
              <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="formBasicEmail">
                  <Form.Label>Email</Form.Label>
                  <Form.Control type="email" placeholder="Enter your email" onChange={this.handleEmailChange.bind(this)}/>
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                  <Form.Label>Password</Form.Label>
                  <Form.Control type="password" placeholder="Enter your password" onChange={this.handlePasswordChange.bind(this)}/>
                </Form.Group>

                <Button variant="primary" type="submit" onClick={this.handleLogin}>
                  Login
                </Button>
              </Form>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}


export default Login;
